def calculer(a, b=10, c):
    return a + b + c

print(calculer(1, 2, 3))

def afficher(*args, **kwargs):
    print(args, kwargs)

afficher(1, 2, 3, name='test', 4)

def factorielle(n):
    return n * factorielle(n - 1)

print(factorielle(5))

def mon_decorateur(func):
    def wrapper():
        print('Avant')
        func()
        print('Apr√®s')
    return wrapper

@mon_decorateur
def dire_bonjour(nom):
    print(f'Bonjour {nom}')

dire_bonjour('Alice')

def traiter(data, callback=print):
    for item in data:
        callback(item)

def transformer(x):
    return x * 2

traiter([1, 2, 3], transformer)
