class Point:
    def __init__(self, x, y):
        self.x = x
        self.y = y

p = Point(3, 4)
print(p.x + p.y)

class Animal:
    def parler(self):
        return 'Son'

a = Animal()
print(a.parler())

try:
    x = 10 / 2
except ZeroDivisionError:
    x = 0
print(int(x))

try:
    x = int('abc')
except ValueError:
    x = -1
print(x)

class Parent:
    valeur = 10

class Enfant(Parent):
    pass

print(Enfant.valeur)

class Nombre:
    def __init__(self, val):
        self.val = val
    def __add__(self, other):
        return Nombre(self.val + other.val)

a = Nombre(5)
b = Nombre(3)
c = a + b
print(c.val)

class Cercle:
    def __init__(self, rayon):
        self._rayon = rayon
    @property
    def diametre(self):
        return self._rayon * 2

c = Cercle(5)
print(c.diametre)

class MaClasse:
    @staticmethod
    def double(x):
        return x * 2

print(MaClasse.double(7))

class A:
    def saluer(self):
        return 'A'

class B(A):
    def saluer(self):
        return 'B' + super().saluer()

print(B().saluer())

class Liste:
    def __init__(self, items):
        self.items = items
    def __len__(self):
        return len(self.items)

l = Liste([1, 2, 3, 4])
print(len(l))

class Container:
    def __init__(self):
        self.items = []
    def __getitem__(self, index):
        return self.items[index] * 2

c = Container()
c.items = [1, 2, 3]
print(c[1])

class A:
    x = 1
class B(A):
    x = 2
class C(A):
    x = 3
class D(B, C):
    pass

print(D.x)

class Temp:
    def __enter__(self):
        return 42
    def __exit__(self, *args):
        pass

with Temp() as val:
    result = val * 2
print(result)
