def maximum(a, b):
    if a > b:
        return a
    return b

print(maximum(8, 5))

def factorielle(n):
    if n <= 1:
        return 1
    return n * factorielle(n - 1)

print(factorielle(5))

def appliquer(f, x):
    return f(x)

resultat = appliquer(lambda n: n * 3, 5)
print(resultat)

def compter(**kwargs):
    return len(kwargs)

print(compter(a=1, b=2, c=3))

def creer_multiplicateur(n):
    def multiplier(x):
        return x * n
    return multiplier

fois_3 = creer_multiplicateur(3)
print(fois_3(5))

def decorator(func):
    def wrapper(x):
        return func(x) * 2
    return wrapper

@decorator
def carre(n):
    return n ** 2

print(carre(3))

def fib(n):
    if n <= 1:
        return n
    return fib(n-1) + fib(n-2)

print(fib(6))
